import React, { Component } from "react";
import './status.css';

class Status extends Component {
    constructor(props) {
        super(props);
        this.state = {
            header: {
                status: null,
                icon: "white fa fa-times",
                colour: "card-red"
            },
            servers: [
            {name: "Platinum: API", status: null},
            {name: "Hydrogen: Web", status: null},
            {name: "Titanium: Desktop", status: null},
            ]
        }
    };

    render() {
        console.log("sdf")
        for (var i= 0; i < this.state.servers.length; i++) {
            return <Card server={this.state.servers[i].name} status={this.state.servers[i].status}/>
