import React, { Component } from 'react';
import './home.css';

import Step0 from './download/step0';
import Step1 from './download/step1';
import Step2 from './download/step2';
import Step3 from './download/step3';

class Home extends Component {
    constructor(props) {
        super(props)
        this.state = {
          currentStep: 0
        }

        this.handleChange = this.handleChange.bind(this)
      }
      handleChange(event) {
          console.log(event)
        const {name, value} = event.target
        this.setState({
          [name]: value
        })    
      }


    render(){
        return (
            <React.Fragment>
                <Step0 currentStep={this.state.currentStep} handleChange={this.handleChange}/>
                <Step1 currentStep={this.state.currentStep} handleChange={this.handleChange}/>
                <Step2 currentStep={this.state.currentStep} handleChange={this.handleChange}/>
                <Step3 currentStep={this.state.currentStep} handleChange={this.handleChange}/>
            </React.Fragment>
        )
    }    
}

export default Home;
